import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";

const Episodes = () => {
  const { id, season_number } = useParams();

  const seasonsDetailed = useSelector((state) => state.series.tvSeason.data);
  return (
    <div>
      <Link
        to={`/tvshow/${id}/season/${season_number}`}
        className=" flex flex-row gap-2 items-center"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          id="Bold"
          viewBox="0 0 24 24"
          width="20"
          height="20"
          className="bg-yellow-500 p-1 rounded-xl"
        >
          <path d="M4.943,5.606,1.024,9.525a3.585,3.585,0,0,0,0,4.95l3.919,3.919a1.5,1.5,0,1,0,2.121-2.121L4.285,13.492l18.25-.023a1.5,1.5,0,0,0,1.5-1.5v0a1.5,1.5,0,0,0-1.5-1.5L4.3,10.492,7.064,7.727A1.5,1.5,0,0,0,4.943,5.606Z" />
        </svg>
        <span className="font-bold">Back to main</span>
      </Link>
      <div className="grid grid-cols-3 gap-2 ">
        {seasonsDetailed.episodes?.map((item, index) => {
          return (
            <Link
              to={`/tvshow/${id}/season/${season_number}/episode/${item.episode_number}`}
              key={index}
              className="grid grid-cols-2 justify-start items-center rounded-2xl  border-t border-b"
            >
              <div className="bg-yellow-500 font-serif font-bold text-sm p-1 rounded-2xl text-black border-2 border-yellow-500 ">
                Episode {item.episode_number}
              </div>
              <div className="text-xs">{item.name}</div>
            </Link>
          );
        })}
      </div>
    </div>
  );
};

export default Episodes;
