import React, { useState } from "react";
import { fetchData, fetchDetails } from "../../utils/utils";
import { Link } from "react-router-dom";

const SearchBar = () => {
  const [search, setSearch] = useState("");
  const [movieResult, setMovieResult] = useState([]);
  const [seriesResult, setSeriesResult] = useState([]);
  const [actorsResult, setActorsResult] = useState([]);

  const handlerSearch = async (e) => {
    const res = await fetchData(
      `search/movie?query=${search}&include_adult=false&language=en-US&page=1`
    );
    fetchData(
      `search/tv?query=${search}&include_adult=false&language=en-US&page=1`
    );
    fetchData(
      `search/person?query=${search}&include_adult=false&language=en-US&page=1`
    );
    setMovieResult(res);
    setSeriesResult(res);
    setActorsResult(res);
  };

  return (
    <div className="relative w-[450px]">
      <div className="flex flex-row gap-2 ">
        <input
          className="h-8 flex items-center  text-start p-2 text-black font-serif text-sm rounded-xl w-[100%] "
          type="search"
          // size="50"
          placeholder="Search Movie / Tv Show / Actors "
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyDown={handlerSearch}
        />
      </div>
      <div className="absolute z-[2] w-[100%] h-[400px] overflow-y-auto bg-gray-300 rounded-xl mt-1 overflow-hidden    ">
        {movieResult &&
          movieResult?.map((movie, index) => {
            return (
              <Link
                to={`/movie/${movie.id}`}
                className="flex flex-row gap-10 h-[70px] m-1 border-2 border-black"
                key={index}
              >
                <img
                  src={`https://image.tmdb.org/t/p/original${movie.poster_path}`}
                  alt={movie.title}
                  className=" "
                />
                <div className="flex flex-col justify-between">
                  <span className="text-sm font-serif text-black font-bold italic">
                    {movie.title}
                  </span>
                  <span className="text-sm font-serif text-black font-bold italic">
                    {movie.release_date}
                  </span>
                </div>
              </Link>
            );
          })}
        {seriesResult &&
          seriesResult?.map((show, index) => {
            return (
              <Link
                to={`/tvshows/${show.id}`}
                className="flex flex-row gap-10 h-[70px] m-1 border-2 border-black"
                key={index}
              >
                <img
                  src={`https://image.tmdb.org/t/p/original${show.poster_path}`}
                  alt={show.title}
                  className=" "
                />
                <div className="flex flex-col justify-between">
                  <span className="text-sm font-serif text-black font-bold italic">
                    {show.title}
                  </span>
                  <span className="text-sm font-serif text-black font-bold italic">
                    {show.release_date}
                  </span>
                </div>
              </Link>
            );
          })}
        {actorsResult &&
          actorsResult?.map((actor, index) => {
            <Link
              to={`/actors/${actor.id}`}
              className="flex flex-row gap-10 h-[70px] m-1 border-2 border-black"
              key={index}
            >
              <img
                src={`https://image.tmdb.org/t/p/original${actor.profile_path}`}
                alt={actor.name}
                className=" "
              />
              <div className="flex flex-col justify-between">
                <span className="text-sm font-serif text-black font-bold italic">
                  {actor.name}
                </span>
                <span className="text-sm font-serif text-black font-bold italic">
                  {actor.known_for_department}
                </span>
              </div>
            </Link>;
          })}
      </div>
    </div>
  );
};

export default SearchBar;
