import React, { useEffect } from "react";
import Modal from "react-modal";
import { openModal, closeModal } from "../../slices/AppSlice";
import { useSelector, useDispatch } from "react-redux";
import ReactPlayer from "react-player";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: 40,
    display: "flex",
    flexDirection: "column",
    gap: 30,
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0, 0, 0, 0.75)",
  },
};
const ModalComponent = (data, trailer = false) => {
  useEffect(() => {
    Modal.setAppElement("#root");
  }, []);
  const dispatch = useDispatch();

  const open = useSelector((state) => state.app.modal.isOpen);

  function openModalFn() {
    dispatch(openModal());
  }

  function closeModalFn() {
    dispatch(closeModal());
  }

  const trailerMovie = useSelector((state) => state.movie.movieTrailer.data);

  return (
    <div>
      {data?.map((item, index) => {
        return (
          <div>
            {trailer && (
              // <div>
              //   {trailerMovie.results
              //     ?.filter(({ type }) => type === "Trailer")
              //     .map((item, index) => {
              //       return (
              <div>
                <button onClick={openModalFn}>View Trailer</button>
                <Modal
                  isOpen={open}
                  onRequestClose={closeModalFn}
                  style={customStyles}
                  contentLabel="Example Modal"
                >
                  <ReactPlayer
                    url={`https://www.youtube.com/watch?v=${item.key}`}
                    controls={true}
                  />
                </Modal>
              </div>
              //   );
              // })}
              // </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default ModalComponent;
