import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchDetails } from "../../../utils/utils";
import { setTvSeason } from "../../../slices/TvShowSlice";
import TvHeader from "../TvHeader";

const SeasonList = () => {
  //   const dispatch = useDispatch();
  //   const { id } = useParams();

  //   useEffect(() => {
  //     async function fetchDataFunction() {
  //       const result = await fetchDetails(`tv/${id}/season/1?language=en-US`);
  //       dispatch(setTvSeason(result));
  //     }

  //     fetchDataFunction();
  //   }, [id]);

  const seasonListing = useSelector((state) => state.series.tvDetail.data);

  return (
    <div className="flex flex-col gap-5">
      <TvHeader />
      {seasonListing.seasons && (
        <div className="flex flex-col gap-10 pl-10 pr-10">
          {seasonListing.seasons?.map((item, index) => {
            return (
              <div className="flex flex-row border-2">
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.name}
                  className="w-[200px]"
                />
                <div>{item.name}</div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default SeasonList;
