import React, { useEffect } from "react";
import { setMovieKeyword } from "../../slices/MovieSlice";
import { fetchDetails } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";

const MovieKeywords = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(`/movie/${id}/keywords`);
      dispatch(setMovieKeyword(result));
    }

    fetchDataFunction();
  }, [id]);

  const moviesKeywords = useSelector((state) => state.movie.movieKeyword.data);

  return (
    <div className="flex flex-col items-start gap-3">
      <span className="font-serif text-xl italic text-yellow-500">
        Keywords
      </span>
      {moviesKeywords && (
        <div className="flex items-start ">
          {moviesKeywords.keywords?.map((item, index) => {
            return (
              <ul key={index} className="flex items-center pr-5">
                <li className="border rounded-xl p-1 text-sm hover:bg-gray-400">
                  {item.name}
                </li>
              </ul>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default MovieKeywords;
