import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { nowPlayingMovies } from "../../slices/AppSlice";
import { fetchData } from "../../utils/utils";

const NowPlaying = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("trending/movie/day?language=en-US");
      dispatch(nowPlayingMovies(res));
    }
    fetchDataFunction();
  }, []);
  const nowPlayingMovie = useSelector((state) => state.app.nowPlaying.data);

  return (
    <div>
      <div>
        <span>Now Playing</span>
      </div>
      <div className="grid grid-cols-5">
        {nowPlayingMovie?.map((item, index) => {
          return (
            <div key={index} className="w-[100px] p-1">
              <img
                src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                alt={item.title}
              />
            </div>
          );
        })}
      </div>
    </div>
  );
};
export default NowPlaying;
