import React, { useEffect, useState } from "react";
import { setSearchedMovie } from "../../slices/MovieSlice";
import { useDispatch, useSelector } from "react-redux";
import { fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";

const SearchBar = () => {
  const dispatch = useDispatch([]);
  const [searchMovie, setSearchMovie] = useState("");
  const { title } = useParams();

  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(
        `search/movie?query=${title}&include_adult=false&language=en-US&page=1`
      );
      dispatch(setSearchedMovie(result));
    }

    fetchDataFunction();
  }, [title]);

  const movieSearch = useSelector((state) => state.movie.searchedMovie.data);

  //   const handlerSearch = (e) => {
  //     setSearchedMovie(e.target.value);
  //   };

  return (
    <div className="flex flex-row items-center bg-white rounded-xl">
      <input
        className="h-8 flex items-center  text-start p-2 text-black font-serif text-sm rounded-xl"
        type="search"
        size="50"
        value={searchMovie}
        placeholder="Search Movie / Tv Show / Actors "
        onChange={(e) => setSearchMovie(e.target.value)}
      />

      <button className="text-black font-bold p-2 bg-yellow-500  rounded-xl">
        Search
      </button>
    </div>
  );
};

export default SearchBar;
