import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setMoviesDetails } from "../../slices/AppSlice";
import { fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";
import Trailer from "./Trailer";

const Details = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(`movie/${id}?language=en-US`);
      dispatch(setMoviesDetails(result));
    }

    fetchDataFunction();
  }, [id]);

  const movieDetails = useSelector((state) => state.app.movieDetail.data);

  return (
    <div className="pl-10 pr-10 pt-5 pb-10">
      {movieDetails && (
        <div className="flex flex-row gap-10 mb-10">
          <img
            src={`https://image.tmdb.org/t/p/original${movieDetails.poster_path}`}
            alt={movieDetails.title}
            className="w-[300px] h-auto rounded-xl"
          />
          <div className="flex flex-col gap-8">
            <div>
              <span className="text-3xl font-serif italic text-yellow-500">
                {movieDetails.title}{" "}
                {movieDetails.release_date &&
                  `(${movieDetails.release_date.split("-")[0]})`}
              </span>
            </div>
            <div className="flex flex-row gap-3 justify-start items-center ">
              <li className="text-sm list-disc list-inside">
                {movieDetails.release_date &&
                  `${movieDetails.release_date.split("-")[2]}`}
                /
                {movieDetails.release_date &&
                  `${movieDetails.release_date.split("-")[1]}`}
                /
                {movieDetails.release_date &&
                  `${movieDetails.release_date.split("-")[0]}`}
              </li>
              {movieDetails.genres?.map((item, index) => {
                return (
                  <ul
                    key={index}
                    className="list-disc inline-block list-inside"
                  >
                    <li className="text-sm">{item.name}</li>
                  </ul>
                );
              })}
              <li className="text-sm list-disc list-inside">
                {Math.floor(movieDetails.runtime / 60)}h{" "}
                {movieDetails.runtime % 60}min
              </li>
            </div>
            <div className="flex flex-row gap-3 items-center">
              {movieDetails.vote_average && (
                <div className="bg-yellow-500 rounded-xl p-1 text-black font-bold text-xl">
                  {movieDetails.vote_average.toFixed(1)}
                </div>
              )}
              <div className="flex flex-wrap">User Score</div>
              <div className="bg-yellow-500 p-1 text-black font-bold rounded-xl">
                <Trailer />
              </div>
            </div>
            <div className="flex flex-col gap-2">
              <span>{item.tagline}</span>
              <span className="text-xl font-serif  underline">Overview</span>
              <span className="text-sm font-serif">
                {movieDetails.overview}
              </span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Details;
