import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setMoviesDetails } from "../../slices/AppSlice";
import { fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";

const MoviesDetails = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchDetails(`movie/${id}?language=en-US`);
      dispatch(setMoviesDetails(res));
    }

    fetchDataFunction();
  }, [id, dispatch]);

  const movieDetails = useSelector((state) => state.app.movieDetail.data);
  return (
    <div>
      {movieDetails &&
        movieDetails.map((movie, index) => {
          <div key={index}>
            <p>Title: {movie.title}</p>
            <p>Overview: {movie.overview}</p>
            <p>Release Date: {movie.release_date}</p>
          </div>;
        })}
    </div>
  );
};

export default MoviesDetails;
