import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  modal: {
    isOpen: false,
    // modalType:''
  },
  appData: {
    data: null,
  },

  trending: {
    data: null,
  },
  nowPlaying: {
    data: null,
  },
};

export const AppSlice = createSlice({
  name: "app",
  initialState,
  reducers: {
    openModal: (state, action) => {
      console.log("deschid modala");
      state.modal.isOpen = true;
    },
    closeModal: (state, action) => {
      console.log("inchid modala");
      state.modal.isOpen = false;
    },
    trendingMoviesToday: (state, action) => {
      state.trendingToday.data = action.payload;
    },
    movieMenu: (state, action) => {
      state.movieNowPlaying.data = action.payload;
    },
  },
});

export const {
  openModal,
  closeModal,
  getCategoryData,
  getAppData,
  trendingMoviesToday,
  movieMenu,
} = AppSlice.actions;

export default AppSlice.reducer;
