import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDetails } from "../../utils/utils";
import { setMovieCast } from "../../slices/AppSlice";
import { useParams } from "react-router-dom";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/scrollbar";
import { Autoplay, Mousewheel, Pagination, Scrollbar } from "swiper/modules";

const Cast = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchDetails(`/movie/${id}/credits?language=en-US`);
      dispatch(setMovieCast(res));
    }

    fetchDataFunction();
  }, [id]);

  const movieCast = useSelector((state) => state.app.moviesActors.data);

  return (
    <>
      {movieCast && (
        <div>
          <span>{movieCast.cast.name}</span>
        </div>
      )}
    </>
  );
};

export default Cast;
