import React from "react";

const DetailedComponent = ({ data, seasons = false, episodes = false }) => {
  return (
    <div>
      {seasons && (
        <div className=" flex flex-row gap-4">
          <img
            src={`https://image.tmdb.org/t/p/original${data.poster_path}`}
            alt={data.name}
            className="w-auto h-[400px]"
          />
          <div className="flex flex-col gap-4">
            <div className="flex flex-row gap-10 items-center">
              <span className="font-serif font-bold text-lg">
                Air Date: {data.air_date.split("-")[2]}.
                {data.air_date.split("-")[1]}.{data.air_date.split("-")[0]}
              </span>
              <div className="font-bold text-xl bg-yellow-500 p-1 rounded-xl text-black">
                {data.vote_average}
              </div>
            </div>

            <span className="font-bold text-xl text-yellow-500">Overview</span>
            <span className="text-sm italic font-serif">{data.overview}</span>
          </div>
        </div>
      )}
      {episodes && (
        <div className=" flex flex-row gap-4">
          <img
            src={`https://image.tmdb.org/t/p/original${data.still_path}`}
            alt={data.name}
            className="w-auto h-[400px]"
          />
          <div className="flex flex-col gap-4">
            <div className="flex flex-row gap-10 items-center">
              <span className="font-serif font-bold text-lg">
                Air Date: {data.air_date.split("-")[2]}.
                {data.air_date.split("-")[1]}.{data.air_date.split("-")[0]}
              </span>
              <div className="font-bold text-xl bg-yellow-500 p-1 rounded-xl text-black">
                {data.vote_average.toFixed[1]}
              </div>
            </div>

            <span className="font-bold text-xl text-yellow-500">Overview</span>
            <span className="text-sm italic font-serif">{data.overview}</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default DetailedComponent;
