import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDetails } from "../../utils/utils";
import { setMovieCast } from "../../slices/AppSlice";
import { useParams } from "react-router-dom";
import { Swiper, SwiperSlide } from "swiper/react";
// import "swiper/css";
// import "swiper/css/scrollbar";
import { Autoplay, Mousewheel, Pagination, Scrollbar } from "swiper/modules";

const Cast = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchDetails(`/movie/${id}/credits?language=en-US`);
      dispatch(setMovieCast(res));
    }

    fetchDataFunction();
  }, [id]);

  const movieCast = useSelector((state) => state.app.moviesActors.data);

  return (
    <>
      {movieCast && (
        <div>
          <span className="bg-yellow-500 p-1 text-black text-2xl font-bold font-serif">
            Top Cast
          </span>
          <Swiper
            direction={"horizontal"}
            slidesPerView={6}
            spaceBetween={0}
            mousewheel={true}
            scrollbar={{
              hide: true,
            }}
            modules={[Mousewheel, Pagination, Scrollbar]}
            className="mt-5"
          >
            {movieCast.cast?.slice(0, 9).map((item, index) => {
              return (
                <SwiperSlide>
                  <div className="flex flex-col h-[350px]">
                    <div key={index} className="h-[50%]">
                      <img
                        src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                        alt={item.name}
                        className="rounded-xl"
                      />
                    </div>
                    <div className="h-[40%] flex gap-3 flex-col  border rounded-xl">
                      <h2 className="font-bold">{item.name}</h2>
                      <p>{item.character}</p>
                    </div>
                  </div>
                </SwiperSlide>
              );
            })}
          </Swiper>
        </div>
      )}
    </>
  );
};

export default Cast;
