import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import { fetchData } from "../../utils/utils";
import { upComingMovies } from "../../slices/AppSlice";
import { useDispatch, useSelector } from "react-redux";

const CarouselComponent = ({ data }) => {
  const dispatch = useDispatch();
  useEffect(() => {
    (async () => {
      await fetchData("titles/x/upcoming").then((res) =>
        dispatch(upComingMovies(res))
      );
    })();
  }, []);
  const upComingMovie = useSelector((state) => state.app.upComing.data);
  console.log(upComingMovie);

  return (
    <Carousel>
      {upComingMovie?.map((item, index) => {
        return (
          <div key={index} className="flex flex-row w-10">
            <img src={item.primaryImage.url} alt={item.primaryImage.caption} />
          </div>
        );
      })}
    </Carousel>
  );
};

export default CarouselComponent;
