import React, { useEffect } from "react";
import { setMovieTrailer } from "../../slices/MovieSlice";
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import ReactPlayer from "react-player";
import YouTubePlayer from "react-player/youtube";

const Trailer = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchData(`movie/${id}/videos?language=en-US`);
      dispatch(setMovieTrailer(result));
    }

    fetchDataFunction();
  }, [id]);

  const trailerMovie = useSelector((state) => state.movie.movieTrailer.data);

  return (
    <div>
      {trailerMovie && (
        <div>
          {trailerMovie
            ?.filter(({ type }) => type === "Trailer")
            .slice(0, 1)
            .map((item, index) => {
              return (
                <Link
                  to={`https://www.youtube.com/watch?v=${item.key}`}
                  className="bg-yellow-500 p-1 text-black font-bold rounded-xl flex flex-row gap-2 items-center"
                  key={index}
                  target="_blank"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    id="Filled"
                    viewBox="0 0 24 24"
                    width="20"
                    height="20"
                  >
                    <path d="M20.492,7.969,10.954.975A5,5,0,0,0,3,5.005V19a4.994,4.994,0,0,0,7.954,4.03l9.538-6.994a5,5,0,0,0,0-8.062Z" />
                  </svg>
                  Watch Trailer
                </Link>
              );
            })}
        </div>
      )}
    </div>
  );
};

export default Trailer;
