import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setMoviesDetails } from "../../slices/AppSlice";
import { fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";

const MoviesDetails = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(`movie/${id}?language=en-US`);
      dispatch(setMoviesDetails(result));
    }

    fetchDataFunction();
  }, [id]);

  const movieDetails = useSelector((state) => state.app.movieDetail.data);
  return (
    <>
      {movieDetails && (
        <div className="pl-10 pr-10">
          <div className="flex flex-row gap-2 ">
            <img
              src={`https://image.tmdb.org/t/p/original${movieDetails.poster_path}`}
              alt={movieDetails.title}
              className="w-[100px] h-auto"
            />
            <span className="text-sm">Overview</span>
            <span className="text-xs font-serif">{movieDetails.overview}</span>
          </div>
        </div>
      )}
    </>
  );
};

export default MoviesDetails;
