import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDetails } from "../../utils/utils";
import { setActorDetail, setActorCredit } from "../../slices/ActorSlice";
import { useParams } from "react-router-dom";

const Biography = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(
        `person/${id}/combined_credits?language=en-US`
      );
      dispatch(setActorCredit(result));
    }

    fetchDataFunction();
  }, [id]);

  const credits = useSelector((state) => state.actorCredit.data);
  const details = useSelector((state) => state.actorDetail.data);

  return (
    <div>
      <span>Biography</span>
      <p>{details.name}</p>
      {credits?.map((item, index) => {
        <div key={index}>{item.original_title}</div>;
      })}
    </div>
  );
};

export default Biography;
