import React, { useEffect } from "react";
import { fetchData } from "../../utils/utils";
import { useDispatch } from "react-redux";
import { movieDetail } from "../../slices/AppSlice";
import { movies } from "../../slices/AppSlice";

const MoviesDetails = () => {
  const dispatch = useDispatch;
  useEffect(() => {
    const fetchMovieDetails = async (movieID) => {
      const detail = await fetchData(`/movie/${movieID}?language=en-US`);
      dispatch(movies(detail));
    };
    fetchMovieDetails();
  }, []);
};

export default MoviesDetails;
