import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Link } from "react-router-dom";
import { Autoplay, Mousewheel, Pagination, Scrollbar } from "swiper/modules";
import "swiper/css";
import ReactPlayer from "react-player";

const SwiperComponent = ({
  data,
  seasonsListing = false,
  id = null,
  movieCard = false,
  seasonCast = false,
  serieCard = false,
  serieCast = false,
  episodeCast = false,
  movieCast = false,
  episodeGuests = false,
  actorsCredits = false,
  movieVideos = false,
}) => {
  return (
    <Swiper
      direction={"horizontal"}
      slidesPerView={7}
      spaceBetween={0}
      autoplay={{
        delay: 5000,
        disableOnInteraction: true,
      }}
      scrollbar={true}
      mousewheel={true}
      modules={[Mousewheel, Pagination, Scrollbar, Autoplay]}
    >
      {data?.map((item, index) => {
        return (
          <SwiperSlide key={index} className="hover:z-[2]">
            {movieCard && (
              <div className="flex flex-col h-[500px] bg-gray-800 m-2 rounded-xl gap-2 hover:scale-[1.1] ">
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.title ? item.title : item.name}
                  className="img rounded-xl h-[70%] flex flex-col justify-center"
                />
              </div>
            )}
            {seasonsListing && (
              <Link to={`/tvshow/${id}/season/${item.season_number}`}>
                <div className="h-[70%] flex gap-2 justify-center p-2">
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                    alt={item.name}
                    className="rounded-xl"
                  />
                </div>
                <div className="h-[30%] font-serif text-gray-400 flex justify-center flex-col mt-2 gap-1 p-1">
                  <span className="text-lg">{item.name}</span>
                  <span className="text-sm text-gray-500">
                    {item.episode_count} Episodes
                  </span>
                </div>
              </Link>
            )}
            {seasonCast && (
              <Link to={`/actor/${item.id}`}>
                <div className="h-[70%] flex gap-2 justify-center p-2">
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                    alt={item.name}
                    className="rounded-xl"
                  />
                </div>
                <div className="h-[30%] font-serif text-gray-400 flex justify-center flex-col mt-2 gap-1 p-1">
                  <span className="text-lg">{item.name}</span>
                  <span className="text-sm text-gray-500">
                    {item.total_episode_count} Episodes
                  </span>
                </div>
              </Link>
            )}
            {serieCard && (
              <Link className="flex flex-col h-[500px] bg-gray-800 m-2 rounded-xl gap-2 hover:scale-[1.1]  ">
                <div className="flex flex-col items-start  justify-between p-2 h-[30%] ">
                  <h2 className="italic font-bold text-lg ">{item.name}</h2>
                  <div className="flex flex-row justify-between items-center w-[100%]">
                    <p className="text-xs text-gray-500">
                      {item.first_air_date}
                    </p>
                    <span className="text-sm font-bold rounded-2xl bg-yellow-400 p-2  text-black">
                      {item.vote_average.toFixed(1)}
                    </span>
                  </div>
                </div>
              </Link>
            )}
            {serieCast && (
              <Link to={`/actor/${id}`}>
                {item.profile_path ? (
                  <div className="h-[60%] p-2">
                    <img
                      src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                      alt={item.name}
                      className="rounded-xl"
                    />
                  </div>
                ) : (
                  <div></div>
                )}
                <div className="flex flex-col gap-2 mt-2 h-[40%]">
                  <h2 className="font-bold">{item.name}</h2>
                  {item.roles && (
                    <>
                      {item.roles?.slice(0, 1).map((role, index) => {
                        return (
                          <div className=" text-gray-500" key={index}>
                            <h4>{role.character}</h4>
                            <h4>{role.episode_count} Episodes</h4>
                          </div>
                        );
                      })}
                    </>
                  )}
                </div>
              </Link>
            )}
            {episodeCast && (
              <Link to={`/actor/${id}`}>
                <div className="h-[60%] p-2">
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                    alt={item.name}
                    className="rounded-xl"
                  />
                </div>
                <div className="flex flex-col gap-2 mt-2 h-[40%]">
                  <h2 className="font-bold">{item.name}</h2>
                  <span>{item.character}</span>
                </div>
              </Link>
            )}
            {movieCast && (
              <Link to={`/actor/${item.id}`} className="h-[320px]">
                <div key={index} className="h-[60%] p-2">
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                    alt={item.name}
                    className="rounded-xl"
                  />
                </div>

                <div className="flex flex-col gap-2 mt-2 h-[40%]">
                  <h2 className="font-bold">{item.name}</h2>
                  <h4 className=" text-gray-500">{item.character}</h4>
                </div>
              </Link>
            )}
            {episodeGuests && (
              <Link to={`/actor/${item.id}`} className="h-[320px]">
                <div key={index} className="h-[60%] p-2">
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                    alt={item.name}
                    className="rounded-xl"
                  />
                </div>
                <div className="flex flex-col gap-2 mt-2 h-[40%]">
                  <h2 className="font-bold">{item.name}</h2>
                  <h4 className=" text-gray-500">{item.character}</h4>
                </div>
              </Link>
            )}
            {actorsCredits && (
              <Link to={`/movie/${id}`}>
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.title}
                  className="w-[120px] h-[170px] rounded-xl mb-2"
                />
                <span className="text-sm">{item.title}</span>
              </Link>
            )}
            {movieVideos && (
              <div>
                <ReactPlayer
                  url={`https://www.youtube.com/watch?v=${item.key}`}
                  controls={true}
                  width={300}
                  height={200}
                />
              </div>
            )}
          </SwiperSlide>
        );
      })}
    </Swiper>
  );
};

export default SwiperComponent;
