import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDetails } from "../../utils/utils";
import { setMovieCast } from "../../slices/AppSlice";
import { useParams } from "react-router-dom";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/scrollbar";
import { Autoplay, Mousewheel, Pagination, Scrollbar } from "swiper/modules";

const Cast = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchDetails(`/movie/${id}/credits?language=en-US`);
      dispatch(setMovieCast(res));
    }

    fetchDataFunction();
  }, [id]);

  const movieCast = useSelector((state) => state.app.moviesActors.data);

  return (
    <>
      {movieCast && (
        <div>
          {movieCast.cast?.map((item, index) => {
            return (
              <Swiper
                direction={"horizontal"}
                slidesPerView={6}
                spaceBetween={0}
                // mousewheel={true}
                // pagination={{
                //   clickable: true,
                // }}
                autoplay={{
                  delay: 3000,
                  disableOnInteraction: false,
                }}
                scrollbar={{
                  hide: true,
                }}
                modules={[Mousewheel, Pagination, Scrollbar, Autoplay]}
              >
                <SwiperSlide>
                  <div className="flex  h-[500px] gap-2 bg-gray-800 m-2 rounded-xl">
                    <div key={index} className="h-[70%]">
                      <img
                        src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                        alt={item.title}
                        className="h-auto rounded-xl"
                      />
                    </div>
                    <div className="flex flex-col items-start gap-2 h-[30%] justify-between p-2">
                      <h2 className="italic font-bold text-lg">{item.name}</h2>
                      <p className="text-xs text-gray-500">
                        {item.first_air_date}
                      </p>
                    </div>
                  </div>
                </SwiperSlide>
              </Swiper>
            );
          })}
        </div>
      )}
    </>
  );
};

export default Cast;
