import React, { useEffect } from "react";
import { actors } from "../../slices/AppSlice";
import { useDispatch, useSelector } from "react-redux";
import { fetchData } from "../../utils/utils";

const Actors = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("/person/popular?language=en-US");
      dispatch(actors(res));
    }
    fetchDataFunction();
  }, []);
  const actorsList = useSelector((state) => state.app.actor.data);

  return (
    <div className="pl-10 flex flex-col gap-2">
      <span className="text-2xl font-serif">Actors</span>
      <div className="grid grid-cols-4 gap-5">
        {actorsList?.map((item, index) => {
          return (
            <div key={index} className="flex flex-col rounded-xl bg-gray-700">
              <img
                className="rounded-xl flex items-center"
                src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                alt={item.name}
              />

              <div className="p-2 text-xl h-24  font-serif">{item.name}</div>
              {item.known_for?.map(() => {
                return <div>{item.known_for.original_title}</div>;
              })}
            </div>
          );
        })}
      </div>
      <button>Load More</button>
    </div>
  );
};

export default Actors;
