import React, { useState } from "react";
import { fetchData } from "../../../utils/utils";

import SearchBar from "../../(utils)/SearchBar";

const MovieSearchBar = () => {
  const [search, setSearch] = useState("");
  const [movieResult, setMovieResult] = useState([]);

  const handlerSearch = async (e) => {
    if (e.key === "Enter") {
      const res = await fetchData(
        `search/movie?query=${search}&include_adult=false&language=en-US&page=1`
      );
      setMovieResult(res);
    }
  };

  return (
    <div className="relative w-[450px]">
      <form>
        <input
          className="relative peer z-10 bg-transparent w-12 h-12 rounded-full border cursor-pointer outline-none pl-12 focus:w-full focus:cursor-text focus:pl-16 focus:pr-4"
          type="search"
          placeholder="Search Movie ..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyDown={handlerSearch}
        />
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          stroke-width="1.5"
          stroke="currentColor"
          class=" absolute inset-y-0 my-auto w-8 h-12  stroke-gray-500 border-r border-transparent "
        >
          <path
            stroke-linecap="round"
            stroke-linejoin="round"
            d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z"
          />
        </svg>
      </form>
      <SearchBar data={movieResult} movieResult={true} />
    </div>
  );
};

export default MovieSearchBar;
