import React, { useEffect } from "react";
import { fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { setSimilarTv } from "../../slices/TvShowSlice";
import { loadingMore } from "../../slices/AppSlice";

const SimilarTv = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(
        `tv/${id}/similar?language=en-US&page=1`
      );
      dispatch(setSimilarTv(result));
    }

    fetchDataFunction();
  }, [id]);

  const similarTvShow = useSelector((state) => state.series.tvSimilar.data);
  const nextPage = useSelector((state) => state.app.loadMore.page);

  const handleLoadMore = async () => {
    dispatch(loadingMore());

    const res = await fetchDetails(
      `/tv/popular?language=en-US&page=${nextPage}`
    );
    dispatch(setSimilarTv([...similarTvShow, ...res]));
  };

  return <div></div>;
};

export default SimilarTv;
