import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setMoviesDetails } from "../../slices/AppSlice";
import { fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";

const MoviesDetails = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchDetails(`movie/${id}`);
      dispatch(setMoviesDetails(res));
    }

    fetchDataFunction();
  }, [id, dispatch]);

  const movieDetails = useSelector((state) => state.app.movieDetail.data);
  return (
    <div>
      <h2>Movie Details</h2>
      {movieDetails && (
        <div>
          <p>Title: {movieDetails.title}</p>
          <p>Overview: {movieDetails.overview}</p>
          <p>Release Date: {movieDetails.release_date}</p>
        </div>
      )}
    </div>
  );
};

export default MoviesDetails;
