import React from "react";

const Sort = () => {
  return (
    <div>
      <div className="bg-yellow-500 p-2 rounded-xl flex flex-col gap-2 font-serif">
        <label className="font-bold text-xl text-black">Sort</label>
        <select className="text-black p-1 border rounded-xl bg-gray-500 text-lg">
          <option selected>Sort by</option>
          <option>Title A to Z</option>
          <option>Title Z to A</option>
          <option>Popularity Ascending</option>
          <option>Popularity Descending</option>
          <option>Release Date Ascending</option>
          <option>Release Date Descending</option>
        </select>
      </div>
    </div>
  );
};

export default Sort;
