import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDetails } from "../../utils/utils";
import { setActorCredit } from "../../slices/ActorSlice";
import { useParams } from "react-router-dom";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css/scrollbar";
import { Mousewheel, Pagination, Scrollbar } from "swiper/modules";

const Biography = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(
        `person/${id}/combined_credits?language=en-US`
      );
      dispatch(setActorCredit(result));
    }

    fetchDataFunction();
  }, [id]);

  const credits = useSelector((state) => state.actors.actorCredit.data);
  const details = useSelector((state) => state.actors.actorDetail.data);

  return (
    <div className="w-[70%] font-serif">
      <div className="flex flex-col gap-3">
        <h1 className="text-2xl font-bold italic">Biography</h1>
        <p className="text-sm">{details.biography}</p>
      </div>
      {credits && (
        <div className="mt-3">
          <span className="text-2xl mt-3">Known For</span>
          <Swiper
            direction={"horizontal"}
            slidesPerView={5}
            spaceBetween={0}
            mousewheel={true}
            modules={[Mousewheel, Pagination, Scrollbar]}
            className="mt-5"
          >
            {credits.cast?.slice(0, 10).map((item, index) => {
              return (
                <SwiperSlide className="h-[320px] p-1 ">
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                    alt={item.title}
                    className="w-[120px] h-[170px] rounded-xl mb-2"
                  />
                  <span className="text-sm">{item.title}</span>
                </SwiperSlide>
              );
            })}
          </Swiper>
        </div>
      )}
    </div>
  );
};

export default Biography;
