import React, { useEffect } from "react";
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { moviesDetails } from "../../slices/AppSlice";

const MoviesDetails = ({ moviesList }) => {
  const dispatch = useDispatch();
  useEffect(() => {
    const fetchMovieDetails = async (movieId) => {
      const res = await fetchData(`movie/${movieId}`);
      dispatch(moviesDetails(res));
    };
    console.log(fetchMovieDetails);
    fetchMovieDetails(moviesList);
  }, []);
  const details = useSelector((state) => state.app.movieDetail.data);
  return (
    <div>
      <h1>{details.id}</h1>
      <p>{details.overview}</p>
    </div>
  );
};

export default MoviesDetails;
