import React, { useEffect } from "react";
import { fetchData } from "../../utils/utils";
import { useDispatch } from "react-redux";
import { movieDetails } from "../../slices/AppSlice";
import { movies } from "../../slices/AppSlice";

const MoviesDetails = () => {
  const dispatch = useDispatch;

  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData(`/movie/movie_id?language=en-US`);
      dispatch(movies(res));
    }

    fetchDataFunction();
  }, []);
};

export default MoviesDetails;
