import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDetails } from "../../utils/utils";
import { setMovieCast, setMoviesDetails } from "../../slices/AppSlice";
import { useParams } from "react-router-dom";

const FullCast = () => {
  const dispatch = useDispatch();
  const { id, title } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchDetails(`/movie/${id}/credits?language=en-US`);
      dispatch(setMovieCast(res));
    }
    dispatch(setMoviesDetails(title));
    fetchDataFunction();
  }, [id, title]);

  const fullMovieCast = useSelector((state) => state.app.moviesActors.data);

  return (
    <>
      <div></div>
      {fullMovieCast && <div>{fullMovieCast.name}</div>}
    </>
  );
};

export default FullCast;
