import React from "react";
import { Link } from "react-router-dom";

const SearchBar = ({
  data,
  movieResult = false,
  serieResult = false,
  actorResult = false,
}) => {
  return (
    <div>
      {data?.map((item, index) => {
        return (
          <div
            className="absolute z-[2] w-[100%] h-[400px] overflow-y-auto bg-gray-300 rounded-xl mt-1 "
            key={index}
          >
            {movieResult && (
              <Link
                to={`/movie/${item.id}`}
                className="flex flex-row gap-10 h-[70px] m-1 border-2 border-black"
              >
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.title}
                  className=" "
                />
                <div className="flex flex-col justify-between">
                  <span className="text-sm font-serif text-black font-bold italic">
                    {item.title}
                  </span>
                  <span className="text-sm font-serif text-black font-bold italic">
                    {item.release_date?.split("-")[0]}
                  </span>
                </div>
              </Link>
            )}
            {serieResult && (
              <Link
                to={`/tvshow/${item.id}`}
                className="flex flex-row gap-10 h-[70px] m-1 border-2 border-black"
                key={index}
              >
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.title}
                  className=" "
                />
                <div className="flex flex-col justify-between">
                  <span className="text-sm font-serif text-black font-bold italic">
                    {item.title}
                  </span>
                  <span className="text-sm font-serif text-black font-bold italic">
                    {item.release_date?.split("-")[0]}
                  </span>
                </div>
              </Link>
            )}
            {actorResult && (
              <Link
                to={`/actor/${item.id}`}
                className="flex flex-row gap-10 h-[70px] m-1 border-2 border-black"
                key={index}
              >
                <img
                  src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                  alt={item.name}
                  className=" "
                />
                <div className="flex flex-col justify-between">
                  <span className="text-sm font-serif text-black font-bold italic">
                    {item.name}
                  </span>
                  <span className="text-sm font-serif text-black font-bold italic">
                    Known for {item.known_for_department}
                  </span>
                </div>
              </Link>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default SearchBar;
