import React, { useState } from "react";
import { fetchData } from "../../../utils/utils";
import { Link } from "react-router-dom";

const SearchBar = ({
  search = "",
  data,
  handlerSearch,
  setSearch,
  movieResult = false,
}) => {
  return (
    <div className="relative w-[450px]">
      <div className="flex flex-row gap-2 ">
        <input
          className="h-8 flex items-center  text-start p-2 text-black font-serif text-sm rounded-xl w-[100%] "
          type="search"
          placeholder="Search Movie / Tv Show / Actors "
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyDown={handlerSearch}
        />
      </div>
      {data?.map((item, index) => {
        <div className="absolute z-[2] w-[100%] h-[400px] overflow-y-auto bg-gray-300 rounded-xl mt-1 hidden">
          {movieResult && (
            <Link
              to={`/movie/${item.id}`}
              className="flex flex-row gap-10 h-[70px] m-1 border-2 border-black"
              key={index}
            >
              <img
                src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                alt={item.title}
                className=" "
              />
              <div className="flex flex-col justify-between">
                <span className="text-sm font-serif text-black font-bold italic">
                  {item.title}
                </span>
                <span className="text-sm font-serif text-black font-bold italic">
                  {item.release_date?.split("-")[0]}
                </span>
              </div>
            </Link>
          )}
        </div>;
      })}
    </div>
  );
};

export default SearchBar;
