import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchData, fetchDetails } from "../../utils/utils";
import { loadingMore } from "../../slices/AppSlice";
import { setMovieReviews } from "../../slices/AppSlice";

const Reviews = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchData(
        `movie/${id}/reviews?language=en-US&page=1`
      );
      dispatch(setMovieReviews(result));
    }

    fetchDataFunction();
  }, [id]);
  const moviesReview = useSelector((state) => state.app.moviesReviews.data);
  const nextPage = useSelector((state) => state.app.loadMore.page);

  const handleLoadMore = async () => {
    dispatch(loadingMore());

    const res = await fetchData(
      `movie/${id}/reviews?language=en-US&page=${nextPage}`
    );
    dispatch(setMovieReviews([...moviesReview, ...res]));
  };

  return (
    <>
      {moviesReview?.map((item, index) => {
        return (
          <div className=" w-[600px] ">
            <div key={index} className="flex flex-col gap-10">
              <div>{item.author}</div>
              <div>{item.content}</div>
            </div>
          </div>
        );
      })}
    </>
  );
};

export default Reviews;
