import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchData } from "../../utils/utils";
import { setMovieCast } from "../../slices/AppSlice";
import { useParams } from "react-router-dom";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/scrollbar";
import { Autoplay, Mousewheel, Pagination, Scrollbar } from "swiper/modules";

const Cast = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData(`/movie/${id}/credits?language=en-US`);
      dispatch(setMovieCast(res));
    }

    fetchDataFunction();
  }, [id]);

  const movieCast = useSelector((state) => state.app.moviesActors.data);

  return (
    <Swiper
      direction={"horizontal"}
      slidesPerView={6}
      spaceBetween={0}
      // mousewheel={true}
      // pagination={{
      //   clickable: true,
      // }}
      autoplay={{
        delay: 3000,
        disableOnInteraction: false,
      }}
      scrollbar={{
        hide: true,
      }}
      modules={[Mousewheel, Pagination, Scrollbar, Autoplay]}
    >
      {movieCast?.map((item, index) => {
        return (
          <SwiperSlide>
            <div key={index}>
              <img
                src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                alt={item.name}
              />
            </div>
          </SwiperSlide>
        );
      })}
    </Swiper>
  );
};

export default Cast;
