import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { trendingMovies } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";

import { Mousewheel, Pagination } from "swiper/modules";

const CarouselComponent = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("trending/movie/day?language=en-US");
      dispatch(trendingMovies(res));
    }
    fetchDataFunction();
  }, []);
  const trendingMovie = useSelector((state) => state.app.trending.data);

  return (
    <>
      <Swiper
        direction={"horizontal"}
        slidesPerView={6}
        spaceBetween={0}
        mousewheel={true}
        pagination={{
          clickable: true,
        }}
        modules={[Mousewheel, Pagination]}
      >
        {trendingMovie?.map((item, index) => {
          return (
            <SwiperSlide>
              <div className="">
                <div key={index}>
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                    alt={item.title}
                    className="w-[100px] h-auto p-2 rounded-xl"
                  />
                </div>
                <div className="p-2">
                  <span className="text-xs rounded-2xl bg-red-500 p-2">
                    {item.vote_average.toFixed(1)}
                  </span>
                  <h2>{item.title}</h2>
                  <p className="text-xs text-gray-400">{item.release_date}</p>
                </div>
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </>
  );
};

export default CarouselComponent;
