import React, { useEffect } from "react";
import Modal from "react-modal";
import { openModal, closeModal } from "../../slices/AppSlice";
import { useSelector, useDispatch } from "react-redux";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: 40,
    display: "flex",
    flexDirection: "column",
    gap: 30,
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0, 0, 0, 0.75)",
  },
};
const ModalComponent = () => {
  // useEffect(() => {
  //   Modal.setAppElement("#root");
  // }, []);
  const dispatch = useDispatch();

  const open = useSelector((state) => state.app.modal.isOpen);
  const modalTypes = useSelector((state) => state.app.modal.modalTypes);

  const handlerOpen = () => {
    dispatch(openModal());
  };

  const handlerClose = () => {
    dispatch(closeModal());
  };

  return (
    <div>
      <Modal style={customStyles} isOpen={open} onRequestClose={handlerClose}>
        <form className="flex flex-col gap-4 items-center text-sm text-black">
          <label>Email Adress</label>
          <input
            type="email"
            placeholder="Enter your email"
            className="border-2 rounded-xl p-1"
          />
          <label>Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            className="border-2 rounded-xl p-1"
          />
          <button
            onClick={handlerClose}
            className="rounded-2xl bg-slate-300 p-2"
          >
            Log In
          </button>
        </form>
      </Modal>
    </div>
  );
};

export default ModalComponent;
