import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { upComingMovies } from "../../slices/AppSlice";

const CarouselComponent = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("titles/x/upcoming");
      dispatch(upComingMovies(res));
    }
    fetchDataFunction();
  }, []);
  const upComingMovie = useSelector((state) => state.app.upComing.data);

  return (
    // <Carousel className="carousel" showThumbs={false}>
    <>
      {upComingMovie?.map((item, index) => {
        return (
          <div key={index}>
            <img
              src={item.primaryImage.url}
              alt={""}
              className="h-[600px] w-auto aspect-auto"
            />
          </div>
        );
      })}
    </>
    // </Carousel>
  );
};

export default CarouselComponent;
