import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchDetails } from "../../../utils/utils";
import { setTvSeason } from "../../../slices/TvShowSlice";

const SeasonList = () => {
  const dispatch = useDispatch();
  const { id } = useParams();

  //   useEffect(() => {
  //     async function fetchDataFunction() {
  //       const result = await fetchDetails(`tv/${id}/season/1?language=en-US`);
  //       dispatch(setTvSeason(result));
  //     }

  //     fetchDataFunction();
  //   }, [id]);

  const seasonListing = useSelector((state) => state.series.tvDetail.data);

  return (
    <div>
      {seasonListing.seasons && (
        <div>
          {seasonListing.seasons?.map((item, index) => {
            return (
              <div>
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.name}
                />
                <div>{item.name}</div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default SeasonList;
