import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

const Career = () => {
  const actorData = useSelector((state) => state.actors.actorCredit.data);

  return (
    <div>
      <div className="flex justify-center mb-3">
        <span className="text-3xl italic font-bold text-yellow-500">
          Acting
        </span>
        {/* <select>
          <option value="filme">Filme</option>
          <option value="filme">Seriale</option>
        </select> */}
      </div>
      <div className="grid grid-cols-[20%_60%_20%] mb-3 font-bold italic text-lg bg-yellow-500 text-black rounded-xl justify-items-center">
        <span>Year</span>
        <span>Movie / Tv Show</span>
        <span>Role</span>
      </div>
      {actorData.cast.map((item, index) => {
        return (
          <div
            className="grid grid-cols-[15%_65%_20%]  w-[100%] p-1 items-center border-b-2 border-gray-700 "
            key={index}
          >
            <div className="flex justify-center items-center">
              {item.release_date &&
              item.sort((a, b) => a.release_date - b.release_date)
                ? item.release_date.split("-")[0]
                : item.first_air_date.sort(
                    (x, y) => x.first_air_date - y.first_air_date
                  )
                ? item.first_air_date.split("-")[0]
                : "-"}
              {item.release_date
                ? item.release_date
                : item.first_air_date
                ? item.first_air_date
                : "-"}
            </div>
            <Link to={`/movie/${item.id}`}>
              <div className="hover:text-gray-500">
                {item.title ? item.title : item.name ? item.name : "-"}
              </div>
            </Link>
            <div>{item.character ? item.character : "-"}</div>
          </div>
        );
      })}
    </div>
  );
};

export default Career;
