import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { trendingTvShows } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/scrollbar";
import { Link } from "react-router-dom";

import { Autoplay, Mousewheel, Pagination, Scrollbar } from "swiper/modules";
import SwiperComponent from "./SwiperComponent";

const CarouselComponentTvNow = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("trending/tv/day?language=en-US");
      dispatch(trendingTvShows(res));
    }
    fetchDataFunction();
  }, []);
  const trendingTvShow = useSelector((state) => state.app.trendingTv.data);

  return <SwiperComponent data={trendingTvShow} serieCard={true} />;
};

export default CarouselComponentTvNow;
