import React from "react";
import { useSelector } from "react-redux";
import SwiperSeazons from "./SwiperSeazons";
import { Link, useParams } from "react-router-dom";

const Seazons = () => {
  const { id, number } = useParams();
  const totalSeasons = useSelector((state) => state.series.tvDetail.data);
  const seasonListing = useSelector((state) => state.series.tvSeason.data);
  return (
    <div>
      <span className="bg-yellow-500 p-1 rounded-l-xl font-bold text-xl text-black font-serif">
        Seasons
      </span>
      <span className="bg-yellow-500 p-1 rounded-r-xl font-bold text-xl text-black font-serif">
        ({totalSeasons.number_of_seasons})
      </span>
      <Link to={`/tvshow/${id}/seasons/${number}`}>
        <SwiperSeazons />
      </Link>
      See All Seasons
    </div>
  );
};

export default Seazons;
