import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchDetails } from "../../../utils/utils";
import { setTvSeason } from "../../../slices/TvShowSlice";
import TvHeader from "../TvHeader";

const SeasonList = () => {
  //   const dispatch = useDispatch();
  //   const { id } = useParams();

  //   useEffect(() => {
  //     async function fetchDataFunction() {
  //       const result = await fetchDetails(`tv/${id}/season/1?language=en-US`);
  //       dispatch(setTvSeason(result));
  //     }

  //     fetchDataFunction();
  //   }, [id]);

  const seasonListing = useSelector((state) => state.series.tvDetail.data);

  return (
    <div className="flex flex-col gap-5 pl-10 pr-10">
      <TvHeader />
      {seasonListing.seasons && (
        <div className="flex flex-col gap-10 ">
          {seasonListing.seasons?.map((item, index) => {
            return (
              <div className="flex flex-row border-2 gap-5 p-2" key={index}>
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.name}
                  className="w-[200px] rounded-2xl"
                />
                <div className="flex flex-col gap-2">
                  <div className="flex flex-row justify-between items-center">
                    <span className="text-yellow-500 font-bold">
                      {item.name}
                    </span>
                    <span className="bg-yellow-500 p-2 rounded-2xl text-black font-bold">
                      {item.vote_average}
                    </span>
                  </div>
                  <span>Overview</span>
                  <span>{item.overview}</span>
                  <span>Total Episodes {item.episode_count}</span>
                  <span>See More</span>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default SeasonList;
