import React, { useEffect } from "react";
import { setMovieKeyword } from "../../slices/AppSlice";
import { fetchData, fetchDetails } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";

const Keywords = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(`movie/${id}/keywords`);
      dispatch(setMovieKeyword(result));
    }

    fetchDataFunction();
  }, [id]);

  const moviesKeywords = useSelector((state) => state.app.movieKeyword.data);

  return (
    <>
      <div className="flex flex-col items-center">
        <span className="">Keywords</span>
        {moviesKeywords && (
          <div className="grid grid-cols-3 items-center justify-center">
            {moviesKeywords.keywords?.map((item, index) => {
              return (
                <ul className="grid grid-cols-3">
                  <li className="border">{item.name}</li>
                </ul>
              );
            })}
          </div>
        )}
      </div>
    </>
  );
};

export default Keywords;
