import React, { useEffect } from "react";
import Details from "./Details";
import Cast from "./TvCast";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import TvReview from "./TvReview";
import { setTvReviews } from "../../slices/TvShowSlice";

const TvDetail = () => {
  const dispatch = useDispatch();
  const tvReviews = useSelector((state) => state.series.tvReviews.data);

  const seeOneReview = async () => {
    dispatch(setTvReviews());
  };

  const { id } = useParams();
  return (
    <div className="flex flex-col gap-10 pl-10 pr-10">
      <Details />
      <Cast />
      <Link to={`/tvshow/${id}/cast`}>
        <span className="font-bold font-serif text-xl bg-gray-800 p-1 rounded">
          Full Cast & Crew
        </span>
      </Link>
      <hr className="opacity-40" />
      <Link to={`/tvshow/${id}/review`}>
        <span className="bg-yellow-500 p-1 rounded-xl font-bold text-xl text-black font-serif">
          Reviews
        </span>
        <div>
          <TvReview />
        </div>
      </Link>
      <hr className="opacity-40" />
    </div>
  );
};

export default TvDetail;
