import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchData } from "../utils/utils";
import { getCategoryData, openModal } from "../slices/AppSlice";
import ModalComponent from "./(utils)/Modal";
import Menu from "./Menu";

const NavBar = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    (async () => {
      await fetchData("titles/utils/titleTypes").then((res) =>
        dispatch(getCategoryData(res))
      );
    })();
  }, []);
  const categoryDataRedux = useSelector((state) => state.app.categoryData.data);
  console.log(categoryDataRedux);
  return (
    <nav className="flex flex-row bg-black h-12 text-white text-xs gap-6 items-center pl-10 pr-10">
      <div className="flex items-center cursor-pointer h-12">
        <img className="h-12" src={require(`../images/imdb.png`)} alt="" />
      </div>
      <div>
        <Menu />
      </div>
      {/* {categoryDataRedux?.map((item, index) => {
        return <div key={index}>{item}</div>;
      })} */}
      <div className="flex flex-row items-center p-5 ">
        <input
          className="h-8 flex items-center  text-start p-2 rounded-l"
          type="search"
          size="50"
          placeholder="Search movie ..."
        />
        <button>
          <svg
            className="bg-white cursor-pointer rounded-r h-8 p-1"
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 50 50"
            width="26px"
            height="26px"
          >
            <path d="M 21 3 C 11.601563 3 4 10.601563 4 20 C 4 29.398438 11.601563 37 21 37 C 24.355469 37 27.460938 36.015625 30.09375 34.34375 L 42.375 46.625 L 46.625 42.375 L 34.5 30.28125 C 36.679688 27.421875 38 23.878906 38 20 C 38 10.601563 30.398438 3 21 3 Z M 21 7 C 28.199219 7 34 12.800781 34 20 C 34 27.199219 28.199219 33 21 33 C 13.800781 33 8 27.199219 8 20 C 8 12.800781 13.800781 7 21 7 Z" />
          </svg>
        </button>
      </div>
      <div>
        <ModalComponent />
      </div>
    </nav>
  );
};

export default NavBar;
