import React, { useEffect } from "react";
import { setMovieTrailer } from "../../slices/MovieSlice";
import { fetchData, fetchDetails } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import ReactPlayer from "react-player";
import YouTubePlayer from "react-player/youtube";

const Trailer = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchData(`movie/${id}/videos?language=en-US`);
      dispatch(setMovieTrailer(result));
    }

    fetchDataFunction();
  }, [id]);

  const trailerMovie = useSelector((state) => state.movie.movieTrailer.data);

  return (
    <div>
      {trailerMovie.results && (
        <div>
          {trailerMovie.results
            ?.filter(({ type }) => type === "Trailer")
            .map((item, index) => {
              return (
                <YouTubePlayer
                  controls={true}
                  key={index}
                  url={`https://www.youtube.com/watch?v=${item.key}`}
                />
              );
            })}
        </div>
      )}
    </div>
  );
};

export default Trailer;
