import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  actor: {
    data: [],
  },
};

export const ActorSlice = createSlice({
  name: "actors",
  initialState,
  reducers: {
    setActor: (state, action) => {
      state.actor.data = action.payload;
    },
  },
});

export const { setActor } = ActorSlice.actions;

export default ActorSlice.reducer;
