import React from "react";
import { Link } from "react-router-dom";

const FullCastComponent = ({
  data,
  movieFullCast = false,
  serieFullCast = false,
  movieCrew = false,
  serieCrew = false,
  id = null,
}) => {
  return (
    <div className="flex flex-col gap-3">
      {data?.map((item, index) => {
        return (
          <div>
            {movieFullCast && (
              <Link to={`/actor/${item.id}`} className="flex flex-col gap-2">
                {item.profile_path ? (
                  <div className="flex flex-row gap-3">
                    <div key={index}>
                      <img
                        src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                        alt={item.name}
                        className="w-[100px] h-auto"
                      />
                    </div>
                    <div className="flex flex-col">
                      <span className="text-bold text-xl">{item.name}</span>
                      <span className="text-sm text-gray-500">
                        {item.character}
                      </span>
                    </div>
                  </div>
                ) : (
                  <></>
                )}
              </Link>
            )}
            {movieCrew && (
              <Link to={`/actor/${item.id}`} className="flex flex-col gap-2">
                {item.profile_path ? (
                  <div className="flex flex-row gap-2">
                    <div key={index}>
                      <img
                        src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                        alt={item.name}
                        className="w-[100px] h-auto"
                      />
                    </div>
                    <div className="flex flex-col gap-2">
                      <span className="text-bold text-xl">{item.name}</span>
                      <span className="text-sm text-gray-500">
                        {item.known_for_department}
                      </span>
                    </div>
                  </div>
                ) : (
                  <></>
                )}
              </Link>
            )}
            {serieFullCast && (
              <Link to={`/actor/${item.id}`} className="flex flex-col gap-2">
                {item.profile_path ? (
                  <div className="flex flex-row gap-2">
                    <img
                      src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                      alt={item.name}
                      className="w-[100px] h-auto"
                    />

                    <div className="flex flex-col">
                      <span className="font-bold text-xl">{item.name}</span>
                      {item.roles && (
                        <div>
                          {item.roles?.map((role, index) => {
                            return (
                              <div key={index} className="flex flex-col">
                                <span className="text-lg text-gray-300 italic">
                                  as
                                </span>
                                <span className="text-lg text-gray-300 italic">
                                  {role.character}
                                </span>
                                <span className="text-sm text-gray-500 ">
                                  ({role.episode_count} Episodes)
                                </span>
                              </div>
                            );
                          })}
                        </div>
                      )}
                    </div>
                  </div>
                ) : (
                  <div></div>
                )}
              </Link>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default FullCastComponent;
