import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setMoviesDetails } from "../../slices/AppSlice";
import { fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";

const MoviesDetails = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchDetails(`movie/${id}`);
      dispatch(setMoviesDetails(res));
    }

    fetchDataFunction();
  }, [id, dispatch]);

  const movieDetails = useSelector((state) => state.app.movieDetail.data);
  return (
    <div>
      {movieDetails?.map((item, index) => {
        return (
          <div key={index}>
            <img
              className="rounded-xl flex items-center"
              src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
              alt={item.title}
            />
          </div>
        );
      })}
    </div>
  );
};

export default MoviesDetails;
